try {
 var request = new HttpRequest(),
 response,
 data;

 request.addHeader('Content-Type: application/json');

 response = request.post('https://localhost/post', value);

 try {
 response = JSON.parse(response);
 }
 catch (error) {
 response = null;
 }

 if (request.getStatus() !== 200 || !('data' in response)) {
 throw 'Unexpected response.';
 }

 data = JSON.stringify(response.data);

 Zabbix.Log(3, '[Webhook Script] response data: ' + data);

 return data;
}
catch (error) {
 Zabbix.Log(3, '[Webhook Script] script execution failed: ' + error);
 throw 'Execution failed: ' + error + '.';
}
